SELECT EMPLOYEE_ID AS 사원번호,FIRST_NAME||' '||LAST_NAME AS 사원명,HIRE_DATE AS 입사일자, CEIL((SYSDATE-HIRE_DATE)/365) AS 근속년수 FROM EMPLOYEES WHERE CEIL((SYSDATE-HIRE_DATE)/365)>=10 ORDER BY 근속년수 DESC;
SELECT FIRST_NAME,MANAGER_ID,DECODE(MANAGER_ID,100,'사원',120,'주임',121,'대리',122,'과장','임원') AS 직급 FROM EMPLOYEES WHERE DEPARTMENT_ID=50;